class HacktoberfestModal {
  constructor() {
    (this.modalShown = !1), this.init();
  }
  init() {
    const t = localStorage.getItem("hacktoberfestModalLastShown"),
      o = new Date().toDateString();
    (!t || t !== o) &&
      (this.createModal(),
      this.showModal(),
      localStorage.setItem("hacktoberfestModalLastShown", o));
  }
  createModal() {
    const t = `<div class="hacktoberfest-modal-container" id="hacktoberfestModal"><div class="hacktoberfest-modal"><img src="img/Pumpkin.webp" alt="Halloween Pumpkin" class="modal-pumpkin"><button class="hacktoberfest-modal-close" onclick="hacktoberfestModal.hideModal()">Ã—</button><div class="hacktoberfest-modal-header"><h2 class="hacktoberfest-modal-title">HACKTOBERFEST IS ALMOST ENDING ðŸŽƒ</h2></div><div class="hacktoberfest-modal-body"><p>Thank you for your participation this year! It was an amazing experience and we hope to be here again next year!</p></div><div class="hacktoberfest-modal-footer"><button class="hacktoberfest-close-button" onclick="hacktoberfestModal.hideModal()">Close</button></div></div></div>`;
    document.body.insertAdjacentHTML("beforeend", t),
      document
        .getElementById("hacktoberfestModal")
        .addEventListener("click", (t) => {
          "hacktoberfest-modal-container" === t.target.className &&
            this.hideModal();
        });
  }
  showModal() {
    const t = document.getElementById("hacktoberfestModal");
    t && (t.classList.add("show"), (this.modalShown = !0));
  }
  hideModal() {
    const t = document.getElementById("hacktoberfestModal");
    t && (t.classList.remove("show"), (this.modalShown = !1));
  }
}
document.addEventListener("DOMContentLoaded", () => {
  new HacktoberfestModal();
});
